mapper.select:

/* checkDupApply */
SELECT
		COUNT(*)
FROM
		EDU_APPLY_INFO
WHERE
		ac_edu_id = #{acEduId}
AND
		judge_no = #{judgeNo}

mapper.insert:

/* insertApply */
INSERT INTO EDU_APPLY_INFO(
	  judge_no
	, judge_name
	, judge_kind
	, ac_edu_id
	, state
	, use_yn
	, reg_date
	, reg_id
)
VALUES(
	  #{judgeNo}
	, #{judgeName}
	, #{judgeKind}
	, #{acEduId}
	, '01'
	, 'Y'
	, NOW()
	, #{judgeNo}
)

mapper.delete:

/* deleteApplyInfoByEduId */
DELETE FROM
		EDU_APPLY_INFO
WHERE
		ac_edu_id IN



	#{acEduId}

mapper.select:
/* selectAdminApplyList */ SELECT @ROWNUM := @ROWNUM - 1 AS ROWNUM , C.* FROM( SELECT A.edu_apply_info_no , A.judge_no , A.judge_name , A.judge_kind , A.ac_edu_id , A.state , A.etc_info , A.use_yn , A.reg_date , A.reg_id , A.apply_confirm_date , A.apply_confirm_id , A.cert_confirm_date , A.cert_confirm_id , B.ac_edu_schedule_no , B.ac_edu_title , CASE WHEN length(B.ac_edu_title) < 36 THEN B.ac_edu_title ELSE RPAD(SUBSTRING(B.ac_edu_title, 1, 12), 16, '*') END AS ac_edu_title_mask , B.ac_edu_institute , B.ac_edu_start_date , B.ac_edu_end_date FROM EDU_APPLY_INFO A JOIN AC_EDU_SCHEDULE B ON A.ac_edu_id = B.ac_edu_id WHERE /* 01: 신청(미확정), 02: 신청확정(확정), 03: 수료확정, 04: 신청취소, 05: 미수료 */ A.state IN
#{code}
AND YEAR(B.ac_edu_start_date) = #{year}
AND B.ac_edu_title LIKE CONCAT('%',#{eduTitle},'%')
AND A.state = #{applyState}
AND A.judge_no LIKE CONCAT('%',#{judgeNo},'%')
ORDER BY A.reg_date DESC ) C, (SELECT @ROWNUM := #{applyListCnt} + 1) NUM

/* selectAdminApplyListCnt */ SELECT COUNT(*) FROM EDU_APPLY_INFO A JOIN AC_EDU_SCHEDULE B ON A.ac_edu_id = B.ac_edu_id WHERE /* 01: 신청(미확정), 02: 신청확정(확정), 03: 수료확정, 04: 신청취소, 05: 미수료 */ A.state IN('01', '02')
AND YEAR(B.ac_edu_start_date) = #{year}
AND B.ac_edu_title LIKE CONCAT('%',#{eduTitle},'%')
AND A.state = #{applyState}
AND A.judge_no LIKE CONCAT('%',#{judgeNo},'%')

mapper.update:

/* updateApplyState */
UPDATE
		  EDU_APPLY_INFO
SET
		  state = #{state}
		, apply_confirm_date = NOW()
		, apply_confirm_id = #{applyConfirmId}
WHERE
		  edu_apply_info_no IN


	#{applyNo}


/* updateConfirmCancel */
UPDATE
		  EDU_APPLY_INFO
SET
		  state = #{state}
		, mod_date = NOW()
		, mod_id = #{modId}
		, apply_confirm_date = NULL
		, apply_confirm_id = NULL
WHERE
		  edu_apply_info_no IN


	#{applyNo}

mapper.delete:

/* deleteApplyInfoByApplyNo */
DELETE FROM
		  EDU_APPLY_INFO
WHERE
		  edu_apply_info_no IN


	#{applyNo}

mapper.select:
/* selectApplyCountState */ SELECT COUNT(*) FROM EDU_APPLY_INFO WHERE state IN
#{state}
AND edu_apply_info_no IN
#{applyNo}

mapper.update:

/* updateApplyInfoByApplyNoArr */
UPDATE
		  EDU_APPLY_INFO
SET
		  state					= #{state}
		, cert_confirm_date		= NULL
		, cert_confirm_id		= NULL
		, mod_date				= NOW()
		, mod_id				= #{modId}
WHERE
		edu_apply_info_no IN


		#{applyNo}
