mapper.delete:

/* deleteCode히히히 */
DELETE FROM
            COMMON_CODE
WHERE
    common_code_no IN


    #{codeNo}

mapper.select:

/* selectCodeNo히히히 */
SELECT common_code_no
FROM COMMON_CODE
WHERE common_code_no IN


    #{codeNo}

mapper.insert:

INSERT INTO COMMON_CODE( group_code_name
                       , group_code
                       , code_name
                       , code
                       , display_order
                       , etc_info
                       , reg_id
                       , reg_date)
VALUES ( #{groupCodeName}
       , #{groupCode}
       , #{codeName}
       , #{code}
       , #{displayOrder}
       , #{etcInfo}
       , #{regId}
       , NOW())

mapper.update:

UPDATE
    COMMON_CODE
SET group_code_name = #{groupCodeName}
  , group_code      = #{groupCode}
  , code_name       = #{codeName}
  , code            = #{code}
  , display_order   = #{displayOrder}
  , etc_info        = #{etcInfo}
WHERE common_code_no = #{commonCodeNo}

UPDATE COMMON_CODE
SET use_state = #{useState}
WHERE
common_code_no IN

    #{codeNo}
