mapper.select:
/* selectJudgeInfo */ SELECT judge_no , judge_name , judge_kind , judge_memo , phone_number , reg_date , mod_date FROM JUDGE_INFO WHERE judge_no = #{judgeNo}
AND judge_name = #{judgeName}
AND judge_kind = #{judgeKind}

mapper.update:

/* updateJudgeInfo */
UPDATE
		JUDGE_INFO
SET
		  judge_name = #{judgeName}
		, phone_number = #{phoneNumber}
		, judge_kind = #{judgeKind}
		, judge_memo = #{judgeMemo}
		, mod_date = NOW()
		, mod_id = #{modId}
WHERE
		judge_no = #{judgeNo}
